ActiveRecord::Base.connection.execute("TRUNCATE TABLE cards RESTART IDENTITY;")
ActiveRecord::Base.connection.execute("TRUNCATE TABLE card_relationships RESTART IDENTITY;")
ActiveRecord::Base.connection.execute("TRUNCATE TABLE rounds RESTART IDENTITY;")
ActiveRecord::Base.connection.execute("TRUNCATE TABLE swaps RESTART IDENTITY;")
Card.create!([
  {id: 1, title: "President", subtitle: "Stay Alive", short_desc: "Stay out of the room with the Bomber.", long_desc: "`Primary`. Blue team wins if you do not gain the `dead` condition.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 1, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 2, title: "Vice President", subtitle: "Backup for the President", short_desc: "Find the President, otherwise stay alive.", long_desc: "`Secondary`. Backup character for the President.  If the President is buried or the President is `dead` before the bomb is activated, the President's Daughter takes on the role of the President, the President's Daughter must be in the opposite room as the Bomber and if applicable card share with the Doctor/Nurse.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 3, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 3, title: "Doctor", subtitle: "Heal the President", short_desc: "You must `card share` with the President.", long_desc: "`Primary`. If the Doctor is present, the Blue Team has the following additional win condition: The President must `card share` with the Doctor before the end of the game.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 5, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 4, title: "Nurse", subtitle: "Backup for the Doctor", short_desc: "Find the Doctor, otherwise `card share` with President", long_desc: "`Secondary`. Backup character for the Doctor.  If the Doctor is buried or the Doctor is `dead`, the Nurse takes on the role of the Doctor, the Nurse must `card share` with the President.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 7, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 5, title: "Negotiator", subtitle: "You are on the Blue Team", short_desc: "May not `color share`, only `card share`.", long_desc: "Negotiator begins the game with the `savvy` condition. May not color share, only card share. (Redundant with fewer than 10 players)", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 9, quantity: 1, required_text: "10+ Players", recommended_text: nil},
  {id: 6, title: "Eris", subtitle: "Sow hate", short_desc: "Once per game, private reveal your card to two players, they now hate each other.", long_desc: "Eris Power: once per game, you may privately reveal your card to two players. You must verbally say to the target players, \"You are in hate with each other.\" Those players gain the `in hate` condition. Players `in hate` replace their victory condition with the following: be in separate rooms as your hater at the end of the game. Eris Power cannot be used on yourself.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 63, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 7, title: "Capitalist", subtitle: "Convert the Red Team", short_desc: "Players who card share with you must trade their card for a Blue Team card. (No effect on Primaries, Secondaries, or Greys)", long_desc: "Any player that card shares with you must trade in their character card for a Blue Team card before the beginning of the next round and before revealing any part of their card to another player. Your power has no effect on `Primaries`, `Secondaries`, or characters not aligned with the Blue Team or Red Team.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 61, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 8, title: "Tuesday Knight", subtitle: "Hug the Bomb", short_desc: "If you card share with the Bomber the game immediately ends. Does not work with Martyr.", long_desc: "Hug Power: if you card share with the Bomber the game immediately ends. Blue Team wins. Does not work on `Secondary`.", color: "Blue", faction: "Blue", strategy: "Can be a short game.", url: "", sort_order: 17, quantity: 1, required_text: "", recommended_text: ""},
  {id: 9, title: "Spy", subtitle: "You are on the Red Team", short_desc: "You are on the opposite team as the color on this card.", long_desc: "The Spy is on the team of the opposite color as the one on their card. The Blue spy is on the Red Team.", color: "Blue", faction: "Red", strategy: "Pretend to be Coy Boy when color revealing to other team.", url: "", sort_order: 11, quantity: 1, required_text: "", recommended_text: ""},
  {id: 10, title: "Paparazzo", subtitle: "Invade Privacy", short_desc: "Attempt to make sure there are no private conversations. Be impolite, but not physical.", long_desc: "Do your best to make sure there are no private conversations. You must try to be as intrusive as possible, including looking over players shoulders at their cards, and even getting on the ground to see the bottoms of cards being passed between players. You cannot physically manipulate other players. If playing with the Privacy Promise variant, ignore the rule as long as you publicly reveal your card to prove you are permitted to be invasive.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 23, quantity: 1, required_text: "", recommended_text: "`Privacy Promise` variant."},
  {id: 11, title: "Angel", subtitle: "Be Honest", short_desc: "Verbally, you must tell the truth.  You can only lie non-verbally.", long_desc: "Angel begins the game with the `honest` condition. Must always tell the truth verbally.  Permitted to lie as long as it's not verbally.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 19, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 12, title: "Agent", subtitle: "\"Papers, please\"", short_desc: "Once per round, flash your ID to force one player to card share. Does not work on `shy` or `paranoid` conditions.", long_desc: "Agent Power: once per round, privately reveal your card to one player, that player must card share with you.  Not effective against conditions that prevent card shares. Player must explain why they cannot card share.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 31, quantity: 1, required_text: "10+ Players", recommended_text: nil},
  {id: 13, title: "Mime", subtitle: "Be Quiet", short_desc: "Do your best to not make any noise.", long_desc: "Acting Role. The mime must attempt to not make any noise for the course of the game.", color: "Blue", faction: "Blue", strategy: "Some people _hate_ to be this role.", url: "", sort_order: 27, quantity: 1, required_text: "", recommended_text: nil},
  {id: 14, title: "Coy Boy", subtitle: "You are on the Blue Team", short_desc: "You may only `color share`.", long_desc: "Coy Boy begins the game with the `coy` condition. May only color share with players, even if compelled to `card share`.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 13, quantity: 1, required_text: "10+ Players", recommended_text: nil},
  {id: 15, title: "Shy Guy", subtitle: "You are on the Blue Team", short_desc: "May not `color share` or `card share`.", long_desc: "Shy Guy begins the game with the `shy` condition. May not reveal any part of their card.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 15, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 16, title: "Security", subtitle: "\"You're going nowhere\"", short_desc: "Once per game, permanently publicly reveal and tackle another player.  Player either gains `tackled` or `torn` condition.", long_desc: "Tackle Power: once per game, you may permanently publicly reveal your card and pick any other player in the room and tell them \"You're going nowhere\". If that player is on the opposite team, they gain the `tackled` condition and may not leave the room as a hostage this round. If you use the power on a member of your team, they gain the `torn` condition, and must permanently publicly reveal regardless of other powers or conditions.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 41, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 17, title: "Paranoid", subtitle: "You are on the Blue Team", short_desc: "You may only `card share`.  And you may only do it once per game.", long_desc: "Paranoid begins the game with the `paranoid` condition. May only `card share` and may only do so once per game.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 57, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 18, title: "Thug", subtitle: "Are you talking to me?", short_desc: "Players who card share with you gain the `coy` condition, they may only `color share` even when compelled to `card share`.", long_desc: "Thug Power: any player that card shares with you acquires the `coy` condition. They may then only color share, even when compelled to card share.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 55, quantity: 1, required_text: "10+ Players.", recommended_text: ""},
  {id: 19, title: "Bomber", subtitle: "Boom", short_desc: "Get to the same room as the President.", long_desc: "`Primary`.  Everyone in the same room as the bomber at the end of the game gains the `dead` condition.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 2, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 20, title: "Martyr", subtitle: "Backup for the Bomber", short_desc: "Find the Bomber, otherwise kill the President.", long_desc: "`Secondary`. Backup character for the Bomber.  If the Bomber is buried or the Bomber is `dead` before the end of the game, the Martyr takes on the role of the Bomber, everyone in the same room as the Martyr at the end of the game gains the `dead` condition.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 4, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 21, title: "Engineer", subtitle: "Repair the Bomb", short_desc: "You must `card share` with the Bomber.", long_desc: "`Primary`. If the Engineer is present, the Red Team has the following additional win condition: The Bomber must `card share` with the Engineer before the end of the game.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 6, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 22, title: "Tinkerer", subtitle: "Backup for the Engineer", short_desc: "Find the Engineer, otherwise `card share` with Bomber.", long_desc: "`Secondary`. Backup character for the Engineer. If the Engineer is buried or `dead`, the Tinkerer takes on the role of the Engineer, the Tinkerer must `card share` with the President.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 8, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 23, title: "Negotiator", subtitle: "You are on the Red Team", short_desc: "May not `color share`, only `card share`.", long_desc: "Negotiator begins the game with the `savvy` condition. May not color share, only card share. (Redundant with fewer than 10 players)", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 10, quantity: 1, required_text: "10+ Players", recommended_text: nil},
  {id: 24, title: "Cupid", subtitle: "Sow Love", short_desc: "Once per game, private reveal your card to two players, they now love each other.", long_desc: "Cupid Power: once per game, you may privately reveal your card to two players. You must verbally say to the target players, \"You are in love with each other.\" Those players gain the `in love` condition. Players `in love` replace their victory condition with the following: be in the same room as your lover at the end of the game. Cupid Power cannot be used on yourself.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 64, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 25, title: "Socialist", subtitle: "Convert the Blue Team", short_desc: "Players who card share with you must trade their card for a Red Team card. (No effect on Primaries, Secondaries, or Greys)", long_desc: "Any player that card shares with you must trade in their character card for a Red Team card before the beginning of the next round and before revealing any part of their card to another player. Your power has no effect on `Primaries`, `Secondaries`, or characters not aligned with the Blue Team or Red Team.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 62, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 26, title: "Dr. Boom", subtitle: "Kill the President", short_desc: "If you card share with the President the game immediately ends. Does not work with VP.", long_desc: "Boom Power: If you card share with the President the game immediately ends. Red Team Wins. Does not work on `Secondary`.", color: "Red", faction: "Red", strategy: "Can be a short game.", url: "", sort_order: 18, quantity: 1, required_text: "", recommended_text: nil},
  {id: 27, title: "Spy", subtitle: "You are on the Blue Team", short_desc: "You are on the opposite team as the color on this card.", long_desc: "The Spy is on the team of the opposite color as the one on their card. The Red spy is on the Blue Team.", color: "Red", faction: "Blue", strategy: "Pretend to be Coy Boy when color revealing to other team.", url: "", sort_order: 12, quantity: 1, required_text: "", recommended_text: ""},
  {id: 28, title: "Paparazzo", subtitle: "Invade Privacy", short_desc: "Attempt to make sure there are no private conversations. Be impolite, but not physical.", long_desc: "Do your best to make sure there are no private conversations. You must try to be as intrusive as possible, including looking over players shoulders at their cards, and even getting on the ground to see the bottoms of cards being passed between players. You cannot physically manipulate other players. If playing with the Privacy Promise variant, ignore the rule as long as you publicly reveal your card to prove you are permitted to be invasive.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 24, quantity: 1, required_text: "", recommended_text: "`Privacy Promise` variant."},
  {id: 29, title: "Angel", subtitle: "Be Honest", short_desc: "Verbally, you must tell the truth.  You can only lie non-verbally.", long_desc: "Angel begins the game with the `honest` condition. Must always tell the truth verbally.  Permitted to lie as long as it's not verbally.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 20, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 30, title: "Agent", subtitle: "\"Papers, please\"", short_desc: "Once per round, flash your ID to force one player to card share. Does not work on `shy` or `paranoid` conditions.", long_desc: "Agent Power: once per round, privately reveal your card to one player, that player must card share with you.  Not effective against conditions that prevent card shares. Player must explain why they cannot card share.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 32, quantity: 1, required_text: "10+ Players", recommended_text: nil},
  {id: 31, title: "Mime", subtitle: "Be Quiet", short_desc: "Do your best to not make any noise.", long_desc: "Acting Role. The mime must attempt to not make any noise for the course of the game.", color: "Red", faction: "Red", strategy: "Some people _hate_ to be this role.", url: "", sort_order: 28, quantity: 1, required_text: "", recommended_text: nil},
  {id: 32, title: "Coy Boy", subtitle: "You are on the Red Team", short_desc: "You may only `color share`.", long_desc: "Coy Boy begins the game with the `coy` condition. May only color share with players, even if compelled to `card share`.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 14, quantity: 1, required_text: "10+ Players", recommended_text: nil},
  {id: 33, title: "Shy Guy", subtitle: "You are on the Red Team", short_desc: "May not `color share` or `card share`.", long_desc: "Shy Guy begins the game with the `shy` condition. May not reveal any part of their card.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 16, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 34, title: "Pirate", subtitle: "\"Argh, walk the Plank\"", short_desc: "Once per game, permanently publicly reveal and name another player who must leave the room this round.", long_desc: "Pirate Power: once per game you may publicly reveal your card and pick any other player in the room and tell them \"Argh, walk the plank\". That player gains the `blasted` condition and must leave the room as one of the hostage(s) at the end of this round.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 42, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 35, title: "Paranoid", subtitle: "You are on the Red Team", short_desc: "You may only `card share`.  And you may only do it once per game.", long_desc: "Paranoid begins the game with the `paranoid` condition. May only `card share` and may only do so once per game.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 58, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 36, title: "Thug", subtitle: "Are you talking to me?", short_desc: "Players who card share with you gain the `coy` condition, they may only `color share` even when compelled to `card share`.", long_desc: "Thug Power: any player that card shares with you acquires the `coy` condition. They may then only color share, even when compelled to card share.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 56, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 37, title: "Agoraphobe", subtitle: "Stay where you are", short_desc: "You win if you are never sent to a different room by anyone.", long_desc: "The agorophobe is afraid of the other room and wins only if they stay in the same room for the course of the game.", color: "Grey", faction: "Grey", strategy: "Leaders can not be hostages. May be helpful to get and stay elected as leader.", url: "", sort_order: 74, quantity: 1, required_text: "", recommended_text: nil},
  {id: 38, title: "Ahab", subtitle: "Kill the white whale", short_desc: "You win if Moby gets the `dead` condition and you do not.", long_desc: "You win if Moby gets the `dead` condition and you do not.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 89, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 39, title: "Decoy", subtitle: "Get shot by the Sniper", short_desc: "You win if the Sniper shoots you at the end of the last round.", long_desc: "You win if the Sniper shoots you at the end of the last round.", color: "Grey", faction: "Grey", strategy: "Work with the Target to get yourself shot by the Sniper.", url: "", sort_order: 85, quantity: 1, required_text: "", recommended_text: nil},
  {id: 40, title: "Gambler", subtitle: "Who won?", short_desc: "At the end of the game, announce which team you think won.", long_desc: "At the end of the game, before players reveal their cards, the gambler announces which team they think won the game. If they are correct they win too.", color: "Grey", faction: "Grey", strategy: "Figure out who the primaries are ASAP.", url: "", sort_order: 69, quantity: 1, required_text: "", recommended_text: nil},
  {id: 41, title: "Hot Potato", subtitle: "If you hold this at the end, you lose", short_desc: "Players who private reveal with you exchange cards. Powers and allegiances transfer; conditions don't. See Cleanse Rule.", long_desc: "Hot Potato Power: any player who privately shares with you trades cards with you immediately. Both of you immediately assume the powers and allegiances of your new card. Conditions are not exchanged. Players are cleansed, i.e. President would need to be healed again if the Doctor/Nurse is in play.", color: "Grey", faction: "Grey", strategy: "Play Coy and ditch this card when you can. Might be easier to get rid of if you can get traded to the other room.", url: "", sort_order: 71, quantity: 1, required_text: "Lots of players.\r\n", recommended_text: "`Privacy Promise` variant.\r\n\r\nBad with Nuclear Tyrant."},
  {id: 42, title: "MI6", subtitle: "Who's who?", short_desc: "`Card share` with the Bomber and the President.", long_desc: "If MI6 `card share`s with the President and the Bomber, or in their absence, their secondary, the Martyr wins the game.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 72, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 43, title: "Minion", subtitle: "Protect your leader", short_desc: "Don't let the leader get usurped.", long_desc: "If the Minion is never in a room while a leader is usurped, the Minion wins the game.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 76, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 44, title: "Mistress", subtitle: "Hide with the President", short_desc: "You win if you are in the same room as the President at the end of the game, provided the Wife is not.", long_desc: "You win if you are in the same room as the President at the end of the game and the Wife is not.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 92, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 45, title: "Moby", subtitle: "Sink the Pequod", short_desc: "You win if Ahab gets the `dead` condition and you do not.", long_desc: "You win if Ahab gets the `dead` condition and you do not.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 90, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 46, title: "Nuclear Tyrant", subtitle: "Stay hidden", short_desc: "May not decline to `card share`. Don't share with the President and the Bomber.", long_desc: "Nuclear Tyrant begins the game with the `foolish` condition.  May not refuse offers to `card share`. Nuclear Tyrant wins the game (and all others lose) if they have not card shared with both the President and the Bomber, or in their absence, their secondary.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 73, quantity: 1, required_text: "", recommended_text: "Bad with Hot Potato."},
  {id: 47, title: "Paper", subtitle: "Paper covers Rock, Scissors cut Paper", short_desc: "End in the same room as Rock, but not Scissors.", long_desc: "Paper wins if in the same room as the Rock but not in the same room as the Scissors.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 87, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 48, title: "Private Investigator", subtitle: "Who's buried?", short_desc: "Deduce the buried card.", long_desc: "At the end of the game, before players reveal their cards (and any other announcements) the Private Investigator must announce the identity of the buried card.  If correct, Private Investigator wins.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 70, quantity: 1, required_text: "Buried card.", recommended_text: "10+ Players."},
  {id: 49, title: "Rock", subtitle: "Rock crushes Scissors, Paper covers Rock", short_desc: "End in the same room as Scissors, but not Paper.", long_desc: "Rock wins if in the same room as the Scissors but not in the same room as the Paper.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 86, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 50, title: "Scissors", subtitle: "Scissors cuts Paper, Rock crushes Scissors", short_desc: "End in the same room as Paper, but not Rock.", long_desc: "Scissors wins if in the same room as the Paper, but not in the same room as the Rock.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 88, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 51, title: "Sniper", subtitle: "Shoot the Target", short_desc: "At the end of the game, announce who you are shooting.", long_desc: "At the end of the game, before players reveal their cards, the Sniper announces who they are shooting (need not be in the same room). Sniper wins if the victim is the Target.  Victim gains the `dead` condition (dead bombers do not detonate)", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 83, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 52, title: "Target", subtitle: "Don't get shot", short_desc: "You win if the Sniper does not shoot you at the end of the last round.", long_desc: "You win if the Sniper does not shoot you at the end of the last round.", color: "Grey", faction: "Grey", strategy: "Work with the Decoy to trick the Sniper into killing him instead.", url: "", sort_order: 84, quantity: 1, required_text: "", recommended_text: ""},
  {id: 53, title: "Traveler", subtitle: "Goodbye", short_desc: "You win if you are sent to a different room at the end of MOST rounds.", long_desc: "You win if you are sent to a different room as a hostage at the end of most rounds.  In a 5 round game, must change rooms 3 times. In a 3 round game must change rooms twice to win.", color: "Grey", faction: "Grey", strategy: "Leaders can not be hostages, do not get elected leader.", url: "", sort_order: 75, quantity: 1, required_text: "", recommended_text: ""},
  {id: 54, title: "Wife", subtitle: "", short_desc: "You win if you are in the same room as the President at the end of the game, provided the Mistress is not.", long_desc: "You win if you are in the same room as the President at the end of the game and the Mistress is not.", color: "Grey", faction: "Grey", strategy: "", url: "", sort_order: 91, quantity: 1, required_text: nil, recommended_text: nil},
  {id: 55, title: "Blue Team", subtitle: "You are on the Blue Team", short_desc: "Separate the President from the Bomber.", long_desc: "If the President does not gain the `dead` condition you win.", color: "Blue", faction: "Blue", strategy: "", url: "", sort_order: 102, quantity: 9, required_text: nil, recommended_text: nil},
  {id: 56, title: "Red Team", subtitle: "You are on the Red Team", short_desc: "Get the Bomber to the same room as the President.", long_desc: "If the President gains the `dead` condition, you win.", color: "Red", faction: "Red", strategy: "", url: "", sort_order: 103, quantity: 9, required_text: nil, recommended_text: nil},
  {id: 57, title: "Clone", subtitle: "Copy someone", short_desc: "You win if the first player with whom you card or color share wins.", long_desc: "You win if the first player with whom you card share wins, succeeding at all their win objectives.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 77, quantity: 1, required_text: "", recommended_text: ""},
  {id: 58, title: "Hero", subtitle: "Save the day", short_desc: "You win if you are in the same room as the President and the Bomber at the end of the game.", long_desc: "Hero wins if in the same room as both the President and the Bomber at the end of the game. If Hero wins, both Red and Blue teams lose.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 79, quantity: 1, required_text: "", recommended_text: ""},
  {id: 59, title: "Prude", subtitle: "Stop the Frotteur", short_desc: "Grab one of the Frotteur's wrists by the end of the game. You may only grab one players wrist during the game.", long_desc: "Prude wins by grabbing one of the Frotteur's wrists before the end of the game. Prude may only grab one player's wrist during the game. Once Prude grabs a wrist they may not let go until the end of the game. Neither player may be a hostage unless they are both hostages.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 82, quantity: 1, required_text: "Touching. 10+ Players.", recommended_text: ""},
  {id: 60, title: "Villain", subtitle: "Now the nightmare's real", short_desc: "You win if you are in the same room as the President and the opposite room as the Bomber.", long_desc: "Victim wins if in the same room as the Bomber at the end of the game.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 80, quantity: 1, required_text: "", recommended_text: ""},
  {id: 61, title: "Rival", subtitle: "Running for President", short_desc: "You win if you are not in the same room as the President at the end of the game.", long_desc: "Rival wins if not in the same room as the President at the end of the game. Opposite of Intern. ", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 93, quantity: 1, required_text: "", recommended_text: ""},
  {id: 62, title: "Frotteur", subtitle: "Touch everyone", short_desc: "If you haven't touched every player by the end of the game you lose. If the Prude grabs your wrist, you lose.", long_desc: "AKA the Gooser. Your mission is to touch every person in the game, and to not be accosted by the Prude.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 81, quantity: 1, required_text: "Touching. 10+ Players.", recommended_text: ""},
  {id: 63, title: "Victim", subtitle: "Follow the Bomber", short_desc: "You win if you are in the same room as the Bomber at the end of the game.", long_desc: "Victim wins if in the same room as the Bomber at the end of the game.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 96, quantity: 1, required_text: "", recommended_text: ""},
  {id: 64, title: "Intern", subtitle: "Follow the President", short_desc: "You win if you are in the same room as the president at the end of the game.", long_desc: "Intern wins if in the same room as the President at the end of the game, regardless of whether the President is `dead`.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 94, quantity: 1, required_text: "", recommended_text: ""},
  {id: 65, title: "Survivor", subtitle: "Avoid the Bomber", short_desc: "You win if you are not in the same room as the Bomber at the end of the game.", long_desc: "Survivor wins if NOT in the same room as the Bomber at the end of the game.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 95, quantity: 1, required_text: "", recommended_text: ""},
  {id: 66, title: "Ambassador", subtitle: "I am an Ambassador!", short_desc: "Permanently publicly reveal at beginning. Immune and can travel freely, do not participate in votes.", long_desc: "Before the game begins Ambassadors must publicly announce \"I am an Ambassador!\" and keep card permanently revealed. Ambassadors have the `immune` condition. Immune to all powers and conditions without exception. Can walk freely between the two rooms, but cannot be hostages nor can they participate in voting. Do not count ambassadors as part of a rooms population for voting purposes.", color: "Blue", faction: "Blue", strategy: "Your strength is in keeping your team members in both rooms on the same page.", url: nil, sort_order: 37, quantity: 1, required_text: "Do not bury.", recommended_text: "11+ Players."},
  {id: 67, title: "Blind", subtitle: "Close your Eyes", short_desc: "Do your best to not open your eyes during the game. Note: Players should not lie to blind players about conditions and powers.", long_desc: "Blind begins the game with the `blind` condition. Must do their best to never open eyes during the game. Be careful you don't hurt yourself.", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 25, quantity: 1, required_text: "Players who are not jerks.", recommended_text: "11+ Players."},
  {id: 68, title: "Clown", subtitle: "Smile!", short_desc: "Do your best to smile at all times.", long_desc: "Clown must try to smile the whole game.", color: "Blue", faction: "Blue", strategy: "Some players cannot stand being the Clown.", url: nil, sort_order: 29, quantity: 1, required_text: "", recommended_text: ""},
  {id: 69, title: "Immunologist", subtitle: "You're `immune`", short_desc: "Immune to all powers and conditions without exception. (May need to privately reveal to prove your immunity)", long_desc: "Immunologist begins with the `immune` condition. Immune to all powers and conditions without exception. Players should never lie about having `immune`. If it becomes a problem Immunologist should `privately reveal` to prove they are `immune`.", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 59, quantity: 1, required_text: "", recommended_text: "Characters with card share powers, like Dealer, Criminal, Thug."},
  {id: 70, title: "Criminal", subtitle: "Never rat on your friends", short_desc: "Players that card share with you gain the `shy` condition.  They may no longer reveal any part of their card to anyone.", long_desc: "Criminal power: Players who card share with the criminal gain the `shy` condition.\r\n\r\n`shy` cancelled by `foolish`.", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 51, quantity: 1, required_text: "", recommended_text: ""},
  {id: 71, title: "Demon", subtitle: "Be Dishonest", short_desc: "Verbally, may only lie. Can only tell the truth non-verbally.", long_desc: "Demon begins the game with the `liar` condition. Must always verbally lie. Can only communicate the truth non-verbally.", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 21, quantity: 1, required_text: "", recommended_text: ""},
  {id: 72, title: "Dealer", subtitle: "...yellow tops. Got red tops...", short_desc: "Players who card share with you gain the `foolish` condition. They may no longer decline to card share.", long_desc: "Dealer Power: any player that card shares with you acquires the `foolish` condition. They may no longer decline an offer to card share.\r\n\r\n`foolish` is cancelled by `shy`.", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 53, quantity: 1, required_text: "", recommended_text: ""},
  {id: 73, title: "Enforcer", subtitle: "You two, share cards", short_desc: "Once per round privately reveal your card to 2 players and tell them to reveal to each other (not you). Not effective on `shy`.", long_desc: "Enforcer Power: once per round you may privately reveal your card to two players.  Verbally tell them \"You *must* reveal your card to each other.\" Those two players must `card share`.  They do not share with you. You can not use this power on yourself but another Enforcer may use theirs on you. If used on a player who is unable to card share (e.g. `shy`) the power is wasted. The target must explain why they cannot share (e.g. I am `shy`)", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 35, quantity: 1, required_text: "", recommended_text: "10+ Players."},
  {id: 74, title: "Exhibitionist", subtitle: "Can not privately share", short_desc: "When sharing, you may only share publicly. Even when forced to share. Can not privately share color or card.", long_desc: "Exhibitionist begins the game with `flashing` condition. May only publicly reveal even when a characters power may force a card share. Can not card share or privately reveal card or color.", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 65, quantity: 1, required_text: "", recommended_text: ""},
  {id: 75, title: "Interrogator", subtitle: "... Yes or no?", short_desc: "Once per round, privately reveal to a player and ask them a single yes/no question. They must answer honestly (possibly non-verbally).", long_desc: "Interrogate Power: once per round you may privately reveal to any player in the room and ask them a single yes/no question to which they must answer honestly. If they sincerely do not know the answer they should indicate so. Answering does not have to be verbal (but can be). Tell the player when you are using your interrogate power.", color: "Blue", faction: "Blue", strategy: "Don't ask about what players plan to do. Changing your plans does not make you dishonest. It makes you fickle.", url: nil, sort_order: 33, quantity: 1, required_text: "", recommended_text: "Remember not to use more than once per round."},
  {id: 76, title: "Mayor", subtitle: "More equal than others", short_desc: "When you publicly reveal your card, your vote counts as two votes.", long_desc: "Mayor votes count double when voting on leaders, provided they publicly reveal their mayor card.", color: "Blue", faction: "Blue", strategy: "Sending your Mayor to an opposition controlled room may be enough to swing the count in your favor.", url: nil, sort_order: 43, quantity: 1, required_text: "", recommended_text: "Helpful in rooms with even numbers of players (i.e. when `n` is a multiple of 4)"},
  {id: 77, title: "Secret Police", subtitle: "You're under arrest!", short_desc: "Alt objective: catch a player playing between rounds, you win and everyone else loses. At least one player must confirm.", long_desc: "Secret Police has an alternative win objective. While Secret Police are in play, doing any action that could be deemed gameplay between rounds is strictly prohibited. If any players discuss gameplay between rounds, publicly reveal your card and yell \"Secret Police! You're under arrest!\" As long as at least one other player in the game can verify that gameplay was occurring between rounds, you win and all other players lose (including the other Secret Police).\r\n", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 47, quantity: 1, required_text: "", recommended_text: "Can speed up gameplay."},
  {id: 78, title: "Psychologist", subtitle: "And how does that make you feel?", short_desc: "Privately reveal to player with  `shy`, `coy`, or `paranoid` condition, they may reveal back, if they do the condition is removed.", long_desc: "Privately reveal your card to a character with the `shy` or `coy` condition. That character may then immediately card share with you. If they do so the `shy` and/or `coy` condition is removed. Furthermore, if someone with the `paranoid` condition card shares with you, the `paranoid` condition is removed.", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 49, quantity: 1, required_text: "", recommended_text: ""},
  {id: 79, title: "Seer", subtitle: "You know all", short_desc: "You begin with the `shy` condition. But, you get to see everyone's card for 30sec before the game begins.", long_desc: "Before the game begins, but after cards are dealt, everyone closes their eyes and publicly reveals for 30 seconds. All players do this regardless of powers, conditions or abilities. Seer begins the game with the `shy` condition.", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 39, quantity: 1, required_text: "Do not bury.", recommended_text: "11+ Players."},
  {id: 80, title: "Usurper", subtitle: "\"I'm the leader\"", short_desc: "Once per game, before the last round, permanent public reveal and you're leader. Can't be usurped by anyone until next round.", long_desc: "Usurper Power: once per game during any round, except the last, you may publicly reveal your card and automatically become the leader. However, you must permanently remain publicly revealed. Usurper can not be usurped during the same round in which they stole leadership, not even by another Usurper. ", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 45, quantity: 1, required_text: "", recommended_text: ""},
  {id: 81, title: "Immunologist", subtitle: "You're `immune`", short_desc: "Immune to all powers and conditions without exception. (May need to privately reveal to prove your immunity)", long_desc: "Immunologist begins with the `immune` condition. Immune to all powers and conditions without exception. Players should never lie about having `immune`. If it becomes a problem Immunologist should `privately reveal` to prove they are `immune`.", color: "Red", faction: "Red", strategy: "", url: nil, sort_order: 60, quantity: 1, required_text: "", recommended_text: "Characters with card share powers, like Dealer, Criminal, Thug."},
  {id: 82, title: "Voyeur", subtitle: "See who's buried", short_desc: "At any time, you may look at the buried card.", long_desc: "Voyeur can look at the buried card at any time. They need not prove that they are the voyeur.", color: "Blue", faction: "Blue", strategy: "", url: nil, sort_order: 67, quantity: 1, required_text: "Buried card.", recommended_text: ""},
  {id: 83, title: "Interrogator", subtitle: "... Yes or no?", short_desc: "Once per round, privately reveal to a player and ask them a single yes/no question. They must answer honestly (possibly non-verbally).", long_desc: "Interrogate Power: once per round you may privately reveal to any player in the room and ask them a single yes/no question to which they must answer honestly. If they sincerely do not know the answer they should indicate so. Answering does not have to be verbal (but can be). Tell the player when you are using your interrogate power.", color: "Red", faction: "Red", strategy: "Don't ask about what players plan to do. Changing your plans does not make you dishonest. It makes you fickle.", url: nil, sort_order: 34, quantity: 1, required_text: "", recommended_text: "Remember not to use more than once per round."},
