  {id: 95, title: "Clown", subtitle: "Smile!", short_desc: "Do your best to smile at all times.", long_desc: "Clown must try to smile the whole game.", color: "Red", faction: "Red", strategy: "Some players cannot stand being the Clown.", url: nil, sort_order: 30, quantity: 1, required_text: "", recommended_text: ""},
  {id: 96, title: "Mayor", subtitle: "More equal than others", short_desc: "When you publicly reveal your card, your vote counts as two votes.", long_desc: "Mayor votes count double when voting on leaders, provided they publicly reveal their mayor card.", color: "Red", faction: "Red", strategy: "Sending your Mayor to an opposition controlled room may be enough to swing the count in your favor.", url: nil, sort_order: 44, quantity: 1, required_text: "", recommended_text: "Helpful in rooms with even numbers of players (i.e. when `n` is a multiple of 4)"},
  {id: 97, title: "Exhibitionist", subtitle: "Can not privately share", short_desc: "When sharing, you may only share publicly. Even when forced to share. Can not privately share color or card.", long_desc: "Exhibitionist begins the game with `flashing` condition. May only publicly reveal even when a characters power may force a card share. Can not card share or privately reveal card or color.", color: "Red", faction: "Red", strategy: "", url: nil, sort_order: 66, quantity: 1, required_text: "", recommended_text: ""},
  {id: 98, title: "Robot", subtitle: "0000001, 00000011,             000000111, 00001111", short_desc: "You win if the first player with whom you share fails to achieve all their win objectives. If you fail to share you lose.", long_desc: "Robot wins if the first player with whom they card share or color share fails to achieve all of their win objectives. If you fail to card or color share with anyone you lose.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 78, quantity: 1, required_text: "", recommended_text: ""},
  {id: 99, title: "Mastermind", subtitle: "", short_desc: "", long_desc: "Mastermind has no text on it. It has a blue side and a red side.\r\n\r\nThere are two ways to play mastermind. Either:\r\n\r\n1. At the end of the game you must name the color (Blue, Red or Grey) of each and every player in the game. If you get them all right, you win and everyone else loses. If you get even one wrong, you lose.\r\n2. You must be elected leader to both rooms throughout the course of the game.\r\n\r\nThe way you are playing must be agreed upon before the cards are dealt.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 101, quantity: 1, required_text: "", recommended_text: ""},
  {id: 100, title: "Maid", subtitle: "There's more to it than that,          you know", short_desc: "You win if you are in the same room as the Butler and the President at the end of the game.", long_desc: "Maid wins if she is in the same room as the Butler and the President at the end of the game.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 98, quantity: 1, required_text: "", recommended_text: "10+ Players."},
  {id: 101, title: "Butler", subtitle: "I couldn't say, sir", short_desc: "You win if you are in the same room as the Maid and the President at the end of the game.", long_desc: "Butler wins if he is in the same room as the Maid and the President at the end of the game.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 97, quantity: 1, required_text: "", recommended_text: "10+ Players."},
  {id: 102, title: "Romeo", subtitle: "O true apothecary!", short_desc: "You win if you are in the same room as Juliet and the Bomber at the end of the game.", long_desc: "Romeo wins if he is in the same room as Juliet and the Bomber at the end of the game.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 99, quantity: 1, required_text: "", recommended_text: "10+ Players."},
  {id: 103, title: "Juliet", subtitle: "O happy dagger!", short_desc: "You win if you are in the same room as Juliet and the Bomber at the end of the game.", long_desc: "Juliet wins if she is in the same room as Romeo and the Bomber at the end of the game.", color: "Grey", faction: "Grey", strategy: "", url: nil, sort_order: 100, quantity: 1, required_text: "", recommended_text: "10+ Players."}
])
